package com.example.go1.play

import android.annotation.SuppressLint
import android.os.Bundle
import android.os.Handler
import android.os.Message
import android.text.TextUtils
import android.util.Log
import android.widget.Toast
import androidx.activity.enableEdgeToEdge
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import com.alipay.sdk.app.EnvUtils
import com.alipay.sdk.app.PayTask
import com.example.go1.R
import com.example.go1.base.BaseActvity
import com.example.go1.databinding.ActivityPayCzactivityBinding
import com.example.go1.pay.PayResult
import com.example.go1.pay.util.OrderInfoUtil2_0
import com.example.go1.play.PayActivity.Companion

class PayCZActivity : BaseActvity<ActivityPayCzactivityBinding>() {

    /**
     * 用于支付宝支付业务的入参 app_id。
     */

    val APPID: String = "2021000118655415"

    /**
     * 用于支付宝账户登录授权业务的入参 pid。
     */

    val PID: String = ""

    /**
     * 用于支付宝账户登录授权业务的入参 target_id。
     */

    val TARGET_ID: String = ""

    /**
     * pkcs8 格式的商户私钥。
     *
     *
     * 如下私钥，RSA2_PRIVATE 或者 RSA_PRIVATE 只需要填入一个，如果两个都设置了，本 Demo 将优先
     * 使用 RSA2_PRIVATE。RSA2_PRIVATE 可以保证商户交易在更加安全的环境下进行，建议商户使用
     * RSA2_PRIVATE。
     *
     *
     * 建议使用支付宝提供的公私钥生成工具生成和获取 RSA2_PRIVATE。
     * 工具地址：https://doc.open.alipay.com/docs/doc.htm?treeId=291&articleId=106097&docType=1
     */

    private val RSA2_PRIVATE: String =
        ""

    private val RSA_PRIVATE: String = ""


    @SuppressLint("HandlerLeak")
    private val mHandler: Handler = object : Handler() {
        @Suppress("unused")
        override fun handleMessage(msg: Message) {
            when (msg.what) {
                PayActivity.SDK_PAY_FLAG -> {
                    val payResult = PayResult(msg.obj as Map<String?, String?>)

                    /**
                     * 对于支付结果，请商户依赖服务端的异步通知结果。同步通知结果，仅作为支付结束的通知。
                     */
                    val resultInfo = payResult.result // 同步返回需要验证的信息
                    val resultStatus = payResult.resultStatus
                    // 判断resultStatus 为9000则代表支付成功
                    if (TextUtils.equals(resultStatus, "9000")) {
                        // 该笔订单是否真实支付成功，需要依赖服务端的异步通知。
//                        ToastUtils.showShort("支付成功");
                        Toast.makeText(this@PayCZActivity, "支付成功", Toast.LENGTH_SHORT).show()
                    } else {
                        // 该笔订单真实的支付结果，需要依赖服务端的异步通知。
//                        ToastUtils.showShort("支付失败" + payResult);
                        Toast.makeText(this@PayCZActivity, "支付成功", Toast.LENGTH_SHORT).show()
                    }
                }

                else -> {}
            }
        }
    }


    /**
     * 支付宝支付业务示例
     */
    fun pay() {
        if (TextUtils.isEmpty(APPID) || (TextUtils.isEmpty(RSA2_PRIVATE) && TextUtils.isEmpty(
                RSA_PRIVATE
            ))
        ) {
            return
        }

        /*
         * 这里只是为了方便直接向商户展示支付宝的整个支付流程；所以Demo中加签过程直接放在客户端完成；
         * 真实App里，privateKey等数据严禁放在客户端，加签过程务必要放在服务端完成；
         * 防止商户私密数据泄露，造成不必要的资金损失，及面临各种安全风险；
         *
         * orderInfo 的获取必须来自服务端；
         */
        val rsa2 = (RSA2_PRIVATE.length > 0)
        val params = OrderInfoUtil2_0.buildOrderParamMap(APPID, rsa2)
        val orderParam = OrderInfoUtil2_0.buildOrderParam(params)

        val privateKey = if (rsa2) RSA2_PRIVATE else RSA_PRIVATE
        val sign = OrderInfoUtil2_0.getSign(params, privateKey, rsa2)
        val orderInfo = "$orderParam&$sign"

        val payRunnable = Runnable {
            val alipay = PayTask(this@PayCZActivity)
            val result = alipay.payV2(orderInfo, true)
            Log.i("msp", result.toString())

            val msg: Message = Message()
            msg.what = PayActivity.SDK_PAY_FLAG
            msg.obj = result
            mHandler.sendMessage(msg)
        }

        // 必须异步调用
        val payThread = Thread(payRunnable)
        payThread.start()
    }


    override fun getLyout(): Int =R.layout.activity_pay_czactivity

    override fun initView() {
          vie.fh.setOnClickListener { finish() }
          vie.btCz.setOnClickListener {
              //开启沙箱
              EnvUtils.setEnv(EnvUtils.EnvEnum.SANDBOX);
              pay();
          }
    }

    override fun initData() {

    }

}